import React, { useEffect } from "react";
import "../styles/style.css"; // Import your existing CSS file

const CursorEffect = () => {
  useEffect(() => {
    const cursorEffect = document.getElementById("cursor-effect");

    const handleMouseMove = (e) => {
      cursorEffect.style.top = `${e.pageY - 50}px`;
      cursorEffect.style.left = `${e.pageX - 50}px`;
    };

    document.addEventListener("mousemove", handleMouseMove);

    return () => {
      document.removeEventListener("mousemove", handleMouseMove); // Cleanup on unmount
    };
  }, []);

  return <div id="cursor-effect"></div>;
};

export default CursorEffect;
