import { useEffect } from "react";
import "../styles/style.css"; // Import your existing CSS file


const CursorEffect = () => {
  useEffect(() => {
    const cursorEffect = document.createElement("div");
    cursorEffect.id = "cursor-effect";
    document.body.appendChild(cursorEffect);

    const handleMouseMove = (e) => {
      cursorEffect.style.position = "absolute";
      cursorEffect.style.top = `${e.pageY - 50}px`;
      cursorEffect.style.left = `${e.pageX - 50}px`;
      cursorEffect.style.width = "100px";
      cursorEffect.style.height = "100px";
      cursorEffect.style.borderRadius = "50%";
      cursorEffect.style.background = "rgba(0, 0, 139, 0.2)";
      cursorEffect.style.pointerEvents = "none";
      cursorEffect.style.zIndex = "9999";
      cursorEffect.style.mixBlendMode = "multiply";
    };

    document.addEventListener("mousemove", handleMouseMove);

    return () => {
      document.removeEventListener("mousemove", handleMouseMove);
      document.body.removeChild(cursorEffect);
    };
  }, []);

  return ; // Nothing needs to be rendered in the DOM, as the element is added dynamically
};

export default CursorEffect;
